mapper.select:
select num, author, title, content, to_char(writeday, 'yyyy/mm/dd')writeday, readcnt
from board
where num = #{num}

mapper.update:
update board 
set readcnt = readcnt + 1 
where num = #{num}

update board 
set num = #{num}, author = #{author}, title = #{title}, content = #{content}
where num = #{num}

mapper.select:
select author, content, to_char(writeday,'yyyy-mm-dd hh:mi:ss')writeday
from boardreply
where contentNum = #{num}
